import { Component, OnInit, Input } from '@angular/core';
import * as qrcode from 'qrcode-generator';
import * as jsPDF from 'jspdf';
import { Router } from '@angular/router';
import { WalletService } from 'src/app/wallet/services/wallet.service';
import { AppConfig } from 'src/app/config/app.config';

@Component({
  selector: 'app-swap-certificate',
  templateUrl: './swap-certificate.component.html',
  styleUrls: ['./swap-certificate.component.css']
})
export class SwapCertificateComponent implements OnInit {

  @Input() routeToContinue: string;
  @Input() params: any;

  publicKey: string;
  transactionHash: string;
  timestamp: string;
  explorerUrl: string;
  qrSrc: any;
  address: string;

  img = 'data:image/png;base64,';

  constructor(
    private router: Router,
    private walletService: WalletService
  ) { }

  ngOnInit() {
    this.publicKey = this.params.transaction.signer.publicKey;
    this.transactionHash = this.params.details.transactionHash.data;
    this.explorerUrl = `http://bob.nem.ninja:8765/#/transfer/${this.params.details.transactionHash.data}`;
    console.log('////// EXPLORER', this.params);
    this.timestamp = `${this.params.transaction.timeWindow.timeStamp._date._year}-${this.params.transaction.timeWindow.timeStamp._date._month}-${this.params.transaction.timeWindow.timeStamp._date._day} ${this.params.transaction.timeWindow.timeStamp._time._hour}:${this.params.transaction.timeWindow.timeStamp._time._minute}:${this.params.transaction.timeWindow.timeStamp._time._second}`;
    console.log('\n\n\n\nValue route:\n', this.routeToContinue, '\n\n\n\nEnd value\n\n');
    this.qrSrc = this.qrCreate();
    this.address = this.params.catapultAccount.address.pretty();
  }

  navToRoute() {
    this.walletService.setAccountInfoNis1(null);
    this.walletService.setAccountMosaicsNis1(null);
    this.routeToContinue = `/${AppConfig.routes.dashboard}`
    this.router.navigate([this.routeToContinue]);
  }

  goToNemExplorer() {
    window.open(this.explorerUrl);
  }

  qrCreate() {
    let qr = qrcode(10, 'H')
    let url = this.explorerUrl;
    qr.addData(url);
    qr.make();

    return qr.createDataURL();
  }

  printCertificate() {
    let doc = new jsPDF({
      orientation: 'landscape',
      unit: 'px',
      format: [800, 475]
    });

    doc.addImage(this.img, 'JPG', 0, 0, 600, 360);
    doc.setFontSize(24);
    doc.setTextColor('#00000');
    doc.text('Swap Process Certificate', 30, 50);
    doc.line(30, 60, 300, 60, 'S');

    doc.setFontSize(14);
    doc.setTextColor('#00000');
    doc.setFontType('bold');
    doc.text('Sirius Account:', 30, 90);
    doc.setFontType('normal');
    doc.text(this.address, 30, 105);

    doc.setFontSize(14);
    doc.setFontType('bold');
    doc.text('NIS1 Timestamp:', 30, 130);
    doc.setFontType('normal');
    doc.text(this.timestamp, 130, 131);

    doc.setFontSize(14);
    doc.setFontType('bold');
    doc.text('NIS1 Public Key:', 30, 155);
    doc.setFontType('normal');
    doc.text(this.publicKey, 30, 170, { maxWidth: 300 });

    doc.addImage(this.qrCreate(), 'jpg', 30, 210, 100, 100);

    doc.setTextColor('#000000');
    doc.setFontType('bold');
    doc.text('NIS1 Transaction Hash:', 140, 230);
    doc.setFontType('normal');
    doc.text(this.transactionHash, 140, 245, { maxWidth: 190 });

    doc.text('Note: the swap process may take a few hours to complete.', 30, 330);

    doc.save('Swap_Certificate.pdf');
  }
}